INSERT INTO `board_review` (`br_no`, `br_board_cate`, `br_user_no`, `br_rank`, `br_img`, `br_img2`, `br_img3`, `br_img4`, `br_img5`, `br_desc`, `br_rt_name`, `br_heart`, `br_comment`, `br_date`) VALUES
(1, 'review', 1, 5, 'r01.jpeg', NULL, NULL, NULL, NULL, '고기 질이 확실히 좋았고 첫 입부터 만족했어요.\n기름이 과하지 않아서 끝까지 깔끔하게 먹었습니다.\n직원분들이 굽기 타이밍도 잘 봐주셔서 편했어요.\n기본 반찬도 신선하고 리필도 빠르게 해주셨고요.\n가격대는 조금 있지만 값어치는 충분했습니다.\n다음엔 다른 부위도 먹으러 재방문할게요.', '정육식당 한우집', 8, 2, '2024-01-11 19:10:00'),
(2, 'review', 2, 4, 'r02.jpeg', NULL, NULL, NULL, NULL, '혼자 먹기 좋은 분위기라 부담 없었습니다.\n가성비도 괜찮아서 종종 올 듯해요.', '동네 백반집', 5, 1, '2024-01-12 12:50:00'),
(3, 'review', 3, 5, 'r03.jpeg', NULL, NULL, NULL, NULL, '우연히 들어갔는데 기대 이상이라 놀랐어요.\n가게는 크지 않지만 회전이 빨라서 오래 기다리진 않았습니다.\n메뉴판이 단순한데 그게 오히려 자신감 같아서 믿음이 갔고요.\n메인 메뉴 양념이 과하지 않고 재료 맛이 살아있었어요.\n반찬도 하나하나 손맛이 느껴져서 숟가락이 멈추질 않았습니다.\n특히 국/찌개 쪽이 진짜 좋았고, 밥이랑 찰떡이었어요.\n가격이 비싸지 않은데도 퀄리티가 유지되는 게 신기했습니다.\n사장님이 동네 손님들 챙기는 느낌이 따뜻했고요.\n다 먹고 나서도 속이 편해서 더 좋았습니다.\n숨은 맛집으로 저장 완료, 주변에도 추천할게요.', '골목 식당', 12, 4, '2024-01-13 20:00:00'),
(4, 'review', 4, 5, 'r04.jpeg', NULL, NULL, NULL, NULL, '멀리서 일부러 찾아갔는데 후회가 없었어요.\n한 그릇에 재료가 푸짐하게 들어가서 만족감이 컸고,\n맛의 밸런스가 좋아서 계속 먹게 됩니다.\n관광지 가격 느낌이 아니라 더 좋았어요.\n전주 가면 다시 들를 집으로 확정!', '전주 비빔밥 명가', 18, 6, '2024-01-14 21:30:00'),
(5, 'review', 5, 4, 'r05.jpeg', NULL, NULL, NULL, NULL, '야식으로 먹기 딱 좋았어요.\n다만 분위기는 호불호가 있을 수 있어요.', '야식 포장마차', 4, 1, '2024-01-15 23:40:00'),
(6, 'review', 6, 4, 'r06.jpeg', NULL, NULL, NULL, NULL, '카페 분위기가 아늑해서 사진 찍기 좋았습니다.\n좌석 간격이 넓어서 대화하기에도 편했고요.\n커피 향이 진하고 산미가 강하지 않아 부담이 없었어요.\n디저트도 적당히 달아서 커피랑 잘 맞았습니다.\n주말에는 사람이 많아 보이니 평일 방문 추천!\n다음에는 브런치 메뉴도 도전해보려고요.', '브런치 카페 거리', 7, 2, '2024-01-16 17:10:00'),
(7, 'review', 7, 5, 'r07.jpeg', NULL, NULL, NULL, NULL, '떡볶이 양념이 진짜 매콤달콤 그 자체였습니다.\n면사리 추가하면 국물 맛이 더 살아나요.\n튀김도 눅눅하지 않고 바삭해서 만족했고,\n매운맛 단계 조절이 가능한 점이 좋았어요.\n분식 좋아하면 여기 한 번은 가봐야 합니다.', '즉석 떡볶이집', 10, 3, '2024-01-17 18:40:00'),
(8, 'review', 8, 5, 'r08.jpeg', NULL, NULL, NULL, NULL, '고기 좋아하는 사람이라면 무조건 만족할 곳이에요.\n첫 주문부터 고기 상태가 좋아서 기대가 올라갔고,\n불판 관리가 잘 돼서 연기도 과하지 않았습니다.\n직원분들이 필요한 타이밍에 알아서 체크해줘서 편했어요.\n겉은 바삭하고 속은 촉촉하게 구워지는 게 포인트였습니다.\n소금/소스/곁들임이 다양해서 물리지 않았고,\n사이드 메뉴도 허투루 만든 느낌이 아니라 좋았습니다.\n가격은 조금 있지만 양이랑 퀄리티 보면 납득돼요.\n단체로 가도 좋겠고, 데이트로도 무난합니다.\n재방문 의사 100%, 다음엔 친구들 데리고 올게요.', '숯불구이 전문점', 15, 5, '2024-01-18 20:50:00'),
(9, 'review', 9, 4, 'r09.jpeg', NULL, NULL, NULL, NULL, '국밥 국물이 진해서 해장에 최고였습니다.\n양도 넉넉해요.', '국밥 거리', 6, 2, '2024-01-19 08:20:00'),
(10, 'review', 10, 4, 'r10.jpeg', NULL, NULL, NULL, NULL, '가격 대비 퀄리티가 괜찮아서 만족했습니다.\n초밥 밥 양이 과하지 않아서 먹기 편했고,\n회도 비린 맛 없이 깔끔했어요.\n기본 찬 구성도 심플하지만 필요한 건 다 있었고요.\n점심 시간엔 대기 있을 수 있으니 참고하세요.\n가성비 초밥 찾는 분들께 추천합니다.', '초밥 전문점', 9, 3, '2024-01-20 13:50:00'),
(11, 'review', 1, 4, 'r11.jpeg', NULL, NULL, NULL, NULL, '두 번째 방문인데도 여전히 만족스러웠어요. 안정적인 맛!', '정육식당 한우집', 6, 1, '2024-01-21 19:20:00'),
(12, 'review', 3, 5, 'r12.jpeg', NULL, NULL, NULL, NULL, '메뉴 구성이 단순하지만 핵심만 잘 잡은 느낌이에요.\n양도 넉넉하고 가격도 부담스럽지 않았습니다.\n간이 과하지 않아서 누구나 좋아할 맛이고,\n밥이랑 같이 먹으면 계속 들어가요.\n가성비 맛집으로 추천합니다.', '골목 식당', 11, 3, '2024-01-22 18:10:00'),
(13, 'review', 11, 4, 'r13.jpeg', NULL, NULL, NULL, NULL, '집밥 느낌 제대로라 편안하게 먹었어요.\n반찬이 매일 바뀌는 느낌이라 질리지 않겠더라고요.\n짜지 않고 담백해서 어른들이 좋아할 스타일입니다.\n양이 많진 않지만 구성 자체가 알차고요.\n근처에 있으면 자주 올 것 같은 집이에요.\n다음엔 찌개 메뉴도 먹어보려 합니다.', '할머니 손맛 식당', 5, 1, '2024-01-23 12:10:00'),
(14, 'review', 12, 5, 'r14.jpeg', NULL, NULL, NULL, NULL, '국물 한 숟갈 뜨자마자 “아 여기 잘한다” 느낌이 왔어요.\n뚝배기라 끝까지 뜨끈하게 먹을 수 있어서 좋았습니다.\n재료가 아낌없이 들어가서 건더기 푸짐했고,\n밥이랑 같이 먹으면 진짜 든든합니다.\n반찬도 대충 나오는 게 아니라 손맛이 느껴졌어요.\n특히 김치가 과하게 시지 않아서 국물이랑 잘 맞고,\n간이 세지 않아도 깊은 맛이 나서 만족했습니다.\n점심 시간에는 근처 직장인들로 붐비는 편이에요.\n그래도 회전이 빨라서 오래 기다리진 않았습니다.\n다음엔 가족 데리고 와서 다른 정식도 먹어볼게요.', '뚝배기 정식집', 9, 3, '2024-01-23 18:40:00'),
(15, 'review', 13, 4, 'r15.jpeg', NULL, NULL, NULL, NULL, '반찬 종류가 많아서 좋았어요.\n든든한 한 끼였습니다.', '시골밥상', 7, 2, '2024-01-24 13:20:00'),
(16, 'review', 14, 4, 'r16.jpeg', NULL, NULL, NULL, NULL, '전체적으로 깔끔하고 정갈한 느낌이었습니다.\n음식이 자극적이지 않아서 편하게 먹었고,\n상차림이 보기 좋게 나와서 대접받는 느낌도 있어요.\n부모님 모시고 오기에도 괜찮아 보입니다.\n다음엔 예약하고 방문해볼게요.', '한상차림', 6, 1, '2024-01-24 19:10:00'),
(17, 'review', 15, 5, 'r17.jpeg', NULL, NULL, NULL, NULL, '된장찌개가 진짜 밥도둑이었습니다.\n구수한 향이 진하고 건더기도 푸짐했어요.\n반찬이 과하지 않은데도 조합이 좋아서 계속 먹게 됩니다.\n가격 대비 만족도가 높아서 재방문 확정!\n점심 때 가면 사람 많을 수 있으니 시간 조절 추천.\n개인적으로는 두부 추가하면 더 맛있을 듯합니다.', '된장마을', 11, 4, '2024-01-25 12:30:00'),
(18, 'review', 16, 4, 'r18.jpeg', NULL, NULL, NULL, NULL, '김치찌개가 진하고 고기도 넉넉해서 만족했어요.', '김치명가', 8, 2, '2024-01-25 18:50:00'),
(19, 'review', 17, 5, 'r19.jpeg', NULL, NULL, NULL, NULL, '새벽에 갔는데도 서비스가 안정적이라 좋았습니다.\n국밥 국물은 진하지만 느끼하지 않고 깔끔했어요.\n고기 양도 충분해서 “건더기 없는 국밥” 느낌이 아니었습니다.\n김치/깍두기랑 같이 먹으면 조합이 완성됩니다.\n24시간이라 야근 후 해장용으로도 괜찮겠더라고요.\n테이블 회전이 빨라서 대기 있어도 금방 들어갑니다.\n사이드로 시키는 수육도 생각보다 괜찮았고,\n가격도 부담스럽지 않아 자주 올 듯합니다.\n양이 많아서 배고픈 날 가면 딱이에요.\n다음엔 친구랑 와서 메뉴 여러 개 나눠 먹어볼게요.', '국밥사랑', 10, 3, '2024-01-26 01:10:00'),
(20, 'review', 18, 4, 'r20.jpeg', NULL, NULL, NULL, NULL, '돌솥이라 끝까지 따뜻해서 좋았어요.\n고소한 맛이 강합니다.', '돌솥비빔', 6, 1, '2024-01-26 13:40:00'),
(21, 'review', 19, 4, 'r21.jpeg', NULL, NULL, NULL, NULL, '집 근처라 기대 없이 갔는데 생각보다 맛있었습니다.\n메뉴가 부담스럽지 않은 가격대라 자주 올 수 있겠어요.\n간이 세지 않아서 매일 먹어도 질리지 않을 느낌이고,\n반찬도 깔끔하게 나와서 좋았습니다.\n주문 후 음식 나오는 시간도 빠른 편이에요.\n동네 맛집으로 저장해두고 종종 들를게요.', '고향식당', 5, 1, '2024-01-27 18:20:00'),
(22, 'review', 20, 3, 'r22.jpeg', NULL, NULL, NULL, NULL, '무난무난한 한식집이에요. 특별하진 않지만 실패도 없습니다.', '한식부엌', 3, 0, '2024-01-27 19:30:00'),
(23, 'review', 21, 5, 'r23.jpeg', NULL, NULL, NULL, NULL, '고기 질이 일정하게 좋아서 믿고 가는 곳입니다.\n이번에도 육즙이 살아있고 잡내가 거의 없었어요.\n기본 반찬이 깔끔해서 고기 맛을 해치지 않고,\n직원분 응대도 빠르니 단체로 가도 문제 없을 듯합니다.\n가격은 있지만 만족도가 더 큰 편이에요.', '정육식당 한우집', 12, 4, '2024-01-28 20:10:00'),
(24, 'review', 22, 4, 'r24.jpeg', NULL, NULL, NULL, NULL, '혼밥러에게 최적화된 느낌!\n가볍게 한 끼 해결하기 좋아요.', '동네 백반집', 6, 1, '2024-01-28 12:00:00'),
(25, 'review', 23, 5, 'r25.jpeg', NULL, NULL, NULL, NULL, '여긴 진짜 숨은 맛집 맞아요.\n한 번 먹고 나면 왜 단골이 많은지 바로 이해됩니다.\n양념이 과하지 않아서 끝까지 맛있게 먹었고,\n반찬도 집에서 만든 느낌이라 정감 있었어요.\n가성비가 좋아서 자주 올 것 같습니다.\n다음엔 점심 말고 저녁에 와서 여유 있게 먹어볼게요.', '골목 식당', 14, 5, '2024-01-29 19:50:00'),
(26, 'review', 24, 5, 'r26.jpeg', NULL, NULL, NULL, NULL, '전주 가면 무조건 여기 들르라고 말하고 싶어요.\n기본기 탄탄한 비빔밥이라 재료 밸런스가 정말 좋습니다.\n고추장 맛이 튀지 않고 고소한 풍미가 살아있어요.\n나물도 숨이 죽지 않고 식감이 살아있어서 좋았고,\n계란과 고기 토핑 조합이 완성도를 높였습니다.\n반찬도 깔끔하게 나오고 리필 요청도 빠르게 대응해주셨어요.\n관광지 느낌 가격이 아니라 더 만족했습니다.\n웨이팅이 있을 수 있지만 회전이 빠른 편이에요.\n매장도 청결해서 가족 여행 때 추천하기 좋아요.\n다음 방문 때는 돌솥 버전도 꼭 먹어보고 싶습니다.', '전주 비빔밥 명가', 13, 4, '2024-01-30 13:10:00'),
(27, 'review', 25, 4, 'r27.jpeg', NULL, NULL, NULL, NULL, '야식으로 딱이에요.\n늦은 시간에도 사람 많더라고요.', '야식 포장마차', 5, 1, '2024-01-30 23:20:00'),
(28, 'review', 26, 4, 'r28.jpeg', NULL, NULL, NULL, NULL, '브런치 메뉴 구성이 생각보다 알찼어요.\n커피도 진해서 같이 먹기 좋았습니다.\n좌석이 편해서 오래 앉아있기에도 괜찮고,\n사진 찍기 좋은 포인트가 많아 만족했어요.\n주말엔 예약하거나 일찍 가는 걸 추천합니다.', '브런치 카페 거리', 7, 2, '2024-01-31 11:40:00'),
(29, 'review', 27, 5, 'r29.jpeg', NULL, NULL, NULL, NULL, '분식 좋아하면 무조건 추천! 양념 맛이 진짜 미쳤어요.', '즉석 떡볶이집', 9, 3, '2024-01-31 18:10:00'),
(30, 'review', 28, 5, 'r30.jpeg', NULL, NULL, NULL, NULL, '고기 굽는 맛이 확실히 다르네요.\n숯불 향이 과하지 않으면서도 풍미가 살아있었고,\n겉바속촉으로 구워져서 만족도가 높았습니다.\n사이드도 맛이 좋아서 같이 시키길 추천해요.\n가격대는 있지만 특별한 날 오기 좋습니다.\n다음엔 다른 부위로도 도전해볼게요.', '숯불구이 전문점', 11, 4, '2024-02-01 20:30:00'),
(31, 'review', 29, 4, 'r31.jpeg', NULL, NULL, NULL, NULL, '국밥 좋아하면 만족할 맛이에요.\n든든하게 한 끼 해결했습니다.', '국밥 거리', 6, 2, '2024-02-01 08:20:00'),
(32, 'review', 30, 4, 'r32.jpeg', NULL, NULL, NULL, NULL, '가성비 초밥으로는 충분히 만족했습니다.\n회가 비리지 않고 밥 양도 적당해요.\n기본 찬도 깔끔하게 나오고,\n점심에 가면 사람 많으니 시간대 조절 추천.\n다음엔 다른 세트도 먹어볼게요.', '초밥 전문점', 8, 3, '2024-02-01 13:50:00'),
(33, 'review', 7, 5, 'r33.jpeg', NULL, NULL, NULL, NULL, '반찬이 푸짐해서 시작부터 만족했습니다.\n전체적으로 간이 과하지 않고 딱 좋았어요.\n국/찌개도 깊은 맛이 나서 밥이 잘 들어갑니다.\n가격 대비 구성이 훌륭해서 자주 올 듯해요.\n주차가 조금 아쉬울 수 있으니 참고하세요.\n가족끼리 방문해도 좋을 식당입니다.', '시골밥상', 9, 3, '2024-02-02 12:20:00'),
(34, 'review', 12, 4, 'r34.jpeg', NULL, NULL, NULL, NULL, '된장 향이 구수하고 자극적이지 않아요.\n밥도둑 인정!', '된장마을', 7, 2, '2024-02-02 19:10:00'),
(35, 'review', 18, 4, 'r35.jpeg', NULL, NULL, NULL, NULL, '정갈한 한 상이라 대접받는 느낌이 있었어요.\n재료가 신선하고 간이 세지 않아 좋았습니다.\n부모님 모시고 가기에도 무난할 듯하고,\n가게도 깨끗해서 더 만족했어요.\n다음엔 예약하고 편하게 방문해볼게요.', '한상차림', 5, 1, '2024-02-03 13:05:00'),
(36, 'review', 9, 5, 'r36.jpeg', NULL, NULL, NULL, NULL, '스시가 전체적으로 신선했고 비린 맛이 거의 없었습니다.\n밥이 질지 않고 적당히 고슬해서 식감이 좋았어요.\n간장/와사비도 과하지 않게 제공되어 조절하기 편했고,\n샤리 크기가 너무 크지 않아서 여러 개 먹기 좋았습니다.\n런치 구성도 괜찮아 보였고, 다음에는 런치로 와볼 생각이에요.\n직원분들이 메뉴 설명을 친절하게 해주셔서 처음 가도 편했습니다.\n대기 줄이 생기긴 하지만 회전이 빨라서 금방 들어갔어요.\n좌석도 깔끔하게 관리되고 있어서 위생적으로 안심됐습니다.\n가격은 조금 있지만 퀄리티 생각하면 납득 가능해요.\n특별한 날 재방문해도 좋을 집입니다.', '사쿠라 스시', 12, 4, '2024-02-03 18:40:00'),
(37, 'review', 21, 4, 'r37.jpeg', NULL, NULL, NULL, NULL, '국물 진하고 면도 탱글해서 만족.\n개인적으로는 차슈가 특히 맛있었어요.', '라멘공방', 8, 2, '2024-02-04 12:30:00'),
(38, 'review', 4, 4, 'r38.jpeg', NULL, NULL, NULL, NULL, '돈카츠가 바삭하고 고기 잡내가 없었습니다.\n소스가 너무 달지 않아서 좋았고,\n밥/샐러드 구성도 기본 이상이에요.\n양이 많아 배부르게 먹었습니다.\n근처에 있으면 종종 들를 것 같아요.', '가츠하우스', 6, 1, '2024-02-04 19:20:00'),
(39, 'review', 15, 4, 'r39.jpeg', NULL, NULL, NULL, NULL, '불맛이 살아있는 중식이라 만족했습니다.\n볶음밥이 기름지지 않고 고슬하게 잘 볶아져서 좋았고,\n짜장/짬뽕도 간이 과하지 않아 먹기 편했어요.\n재료가 넉넉해서 한 그릇 먹고 나면 든든합니다.\n점심 피크에는 붐빌 수 있으니 일찍 가는 걸 추천.\n가성비 중식 찾으면 여기 괜찮아요.', '홍콩반점', 9, 2, '2024-02-05 13:10:00'),
(40, 'review', 27, 5, 'r40.jpeg', NULL, NULL, NULL, NULL, '마라 향이 너무 세지 않고 적당해서 입문자도 괜찮을 듯해요.\n재료 선택 폭이 넓어서 취향대로 조합하기 좋았습니다.\n국물에서 잡내가 안 나고 깔끔하게 매운맛이라 만족했고,\n면/야채/고기 모두 신선하게 관리되는 느낌이었어요.\n맵기 단계가 잘 나뉘어 있어서 조절도 쉽고요.\n소스바가 잘 되어 있어 디핑해서 먹는 재미도 있었습니다.\n양이 많아서 두 명이 나눠 먹어도 충분하겠더라고요.\n가격은 선택 재료에 따라 달라지지만 납득 가능했습니다.\n매장도 깨끗하고 환기가 잘 돼서 냄새가 덜 배어요.\n다음엔 다른 토핑 조합으로 또 먹어볼게요.', '마라천국', 11, 3, '2024-02-05 19:05:00'),
(41, 'review', 2, 4, 'r41.jpeg', NULL, NULL, NULL, NULL, '딤섬이 촉촉하고 종류도 다양합니다.\n차랑 같이 먹기 좋았어요.', '딤섬가든', 7, 1, '2024-02-06 12:40:00'),
(42, 'review', 10, 5, 'r42.jpeg', NULL, NULL, NULL, NULL, '스테이크 굽기가 딱 원하는 정도로 나와서 좋았습니다.\n겉은 바삭하고 속은 촉촉해서 육즙이 살아있었어요.\n소스가 과하게 달지 않아 고기 맛을 잘 살려주고,\n사이드도 대충 만든 느낌이 아니라 만족도가 높았습니다.\n가격대는 있지만 특별한 날엔 충분히 가치 있어요.\n다음엔 다른 부위로도 먹어보고 싶습니다.', '스테이크하우스', 14, 5, '2024-02-06 20:10:00'),
(43, 'review', 23, 4, 'r43.jpeg', NULL, NULL, NULL, NULL, '파스타 면이 잘 익어서 식감 좋고 소스도 깔끔했어요.', '파스타룸', 8, 2, '2024-02-07 18:10:00'),
(44, 'review', 6, 4, 'r44.jpeg', NULL, NULL, NULL, NULL, '수제버거 패티가 두툼해서 한 입부터 만족했습니다.\n번이 눅눅하지 않고 고소해서 좋았고,\n감자튀김도 바삭하게 나와서 같이 먹기 좋았어요.\n소스가 과하지 않아 끝까지 물리지 않았습니다.\n재방문해서 다른 메뉴도 먹어보려고요.', '버거스팟', 9, 2, '2024-02-07 12:20:00'),
(45, 'review', 5, 5, 'r45.jpeg', NULL, NULL, NULL, NULL, '양념이 진짜 중독적이에요.\n다 먹고 나서도 계속 생각나는 맛!', '국민떡볶이', 13, 4, '2024-02-08 18:30:00'),
(46, 'review', 17, 4, 'r46.jpeg', NULL, NULL, NULL, NULL, '튀김이 바삭하고 기름 냄새가 심하지 않았어요.\n모둠 구성도 다양해서 골라 먹는 재미가 있습니다.\n떡볶이랑 같이 먹으면 조합이 완성되고,\n간이 과하지 않아 부담이 없었습니다.\n양이 넉넉해서 친구랑 나눠 먹기 좋아요.\n다음엔 다른 메뉴도 같이 시켜볼게요.', '튀김골목', 7, 2, '2024-02-08 19:10:00'),
(47, 'review', 1, 4, 'r47.jpeg', NULL, NULL, NULL, NULL, '떡튀순 세트는 언제나 정답… 여기 구성 괜찮아요.', '떡튀순집', 10, 3, '2024-02-09 12:10:00'),
(48, 'review', 14, 4, 'r48.jpeg', NULL, NULL, NULL, NULL, '라떼가 부드럽고 밸런스가 좋아서 만족했습니다.\n좌석이 편해서 노트북 작업하기도 괜찮고,\n음악 볼륨도 적당해서 오래 머물기 좋았어요.\n디저트도 함께 주문하면 조합이 괜찮습니다.\n근처 들를 때 재방문할게요.', '라떼하우스', 6, 1, '2024-02-09 15:20:00'),
(49, 'review', 29, 5, 'r49.jpeg', NULL, NULL, NULL, NULL, '드립 향이 좋아서 커피만으로도 만족했어요.\n작업하기도 괜찮습니다.', '브루잉랩', 9, 2, '2024-02-10 11:05:00'),
(50, 'review', 20, 5, 'r50.jpeg', NULL, NULL, NULL, NULL, '수제 케이크라 그런지 크림이 느끼하지 않고 깔끔했습니다.\n시트가 퍽퍽하지 않고 촉촉해서 식감이 좋았고요.\n단맛이 과하지 않아 커피랑 같이 먹기 딱이었습니다.\n조각 케이크 크기도 적당해서 여러 개 맛보기 좋았어요.\n매장 분위기가 아늑해서 오래 앉아 있어도 편했습니다.\n포장도 깔끔하게 해줘서 선물용으로도 괜찮겠더라고요.\n직원분 응대도 친절해서 기분 좋게 나왔습니다.\n가격은 조금 있지만 퀄리티 생각하면 납득돼요.\n다음엔 다른 시즌 메뉴도 먹어보고 싶습니다.\n디저트 좋아하는 분들이면 만족할 확률 높아요.', '케이크공방', 12, 4, '2024-02-10 16:40:00'),
(51, 'review', 3, 4, 'r51.jpeg', NULL, NULL, NULL, NULL, '마카롱 꼬끄가 쫀득해서 식감이 좋았습니다.\n필링이 너무 달지 않아 여러 개 먹어도 부담이 덜했고,\n맛 종류도 다양해서 고르는 재미가 있어요.\n보관 방법 안내도 잘 되어 있어 초보도 편했습니다.\n선물용으로도 무난해서 종종 살 것 같아요.\n다음엔 다른 맛 조합으로 사보려고요.', '마카롱샵', 7, 2, '2024-02-11 14:15:00'),
(52, 'review', 25, 4, 'r52.jpeg', NULL, NULL, NULL, NULL, '여러 나라 메뉴를 한 번에 즐길 수 있어서 좋았어요.\n전체적으로 무난한 맛이라 호불호가 적을 듯하고,\n좌석이 넓어서 모임으로 가기 괜찮습니다.\n가격대도 과하지 않아 부담이 덜했고요.\n다음엔 다른 메뉴 조합으로 다시 먹어볼게요.', '글로벌푸드', 6, 1, '2024-02-11 18:10:00');

